import React from 'react';
import CardList from '../CardList'; //child
import SearchBox from '../SearchBox' //child
import Scroll from '../Scroll'

class Search extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            professors: [],
            searchedName:'',
            searchfield: ''
        }
    }

    componentDidMount() {
        fetch('http://localhost:3000/prof')
            .then(response => response.json())
            .then(users => this.setState({professors: users}));
    }

    onSearchChange = (event) => { //whenever it gets changed
        this.setState({searchfield: event.target.value}) //update "serachfiled is event.target.value"
    }

    // filterdProfs = () =>{
    //     this.state.professors.filter(prof => {
    //     console.log(prof);
    //     return prof.name.toLowerCase().includes(this.state.searchfield.toLowerCase())
    // })
    // }

    render() {
        const filterdProfs = this.state.professors.filter(prof => {
            console.log(prof);
            //"filter" is a function to go thorough array in "robots from State", having a parameter "robot"
            return prof.name.toLowerCase().includes(this.state.searchfield.toLowerCase())
            //return the "robot"'s name if it is included"serachfield's strings, setting lowercase every words
        })
        return (
            <div className="measure">
                <label className="db fw6 lh-copy f2" htmlFor="name">Easy or Hard</label>
                <SearchBox onSearchChange={this.onSearchChange}/>
                <Scroll>
                    <CardList professors={filterdProfs} onRouteChange={this.props.onRouteChange} onNameChange={this.props.onNameChange}/>
                </Scroll>
            </div>
        );
    }
}


export default Search;
