import React, { Component } from "react";
import ImageLinkForm from "../ImageLinkForm/ImageLinkForm";


class Form extends Component {
    constructor(props){
        super(props);
        this.state = {
            id: '',
            name: '',
            value:[],
            category: '',
            grade: '',
            book: '',
            attendance:'',
            classstyle: '',
            comments: ''
        };
        console.log(this.state.value)
    }

    onHandleChange = (event) => {
        this.setState({value: event.target.value})
    }

    onNameChange = (event) => {
        this.setState({name: event.target.value}) // updated signInEmail from <input />
    }

    onClassChange = (event) => {
        this.setState({classstyle: event.target.value}) // updated signInEmail from <input />
    }

    onCommentsChange = (event) => {
        this.setState({comments: event.target.value}) // updated signInEmail from <input />
    }


    handleContentsChange = changeEvent => {
        this.setState({
            contents: changeEvent.target.value
        });
        console.log(this.state.contents)
    };

    handleEasinessChange = changeEvent => {
        this.setState({
        easiness: changeEvent.target.value
        });
        console.log(this.state.easiness)
    };

    handleBookChange = changeEvent => {
        this.setState({
            book: changeEvent.target.value
        });
    };

    handleAttendanceChange = changeEvent => {
        this.setState({
            attendance: changeEvent.target.value
        });
    };

    onSubmitForm =() => {
        fetch('http://localhost:3000/register', {
            method: 'post',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({ // Send email and password updated at "onEmailChange" and "onPasswordChange" to the database through JSON.stringify
                name: this.state.name,
                coursenumber:this.state.coursenumber,
                easiness: this.state.easiness,
                contents: this.state.contents,
                grade: this.state.grade,
                book: this.state.book,
                attendance:this.state.attendance,
                classstyle: this.state.classstyle,
                comments: this.state.comments
            })
        })
            .then(response => response.json()) // Get response through json, and get data by ".then"
            .then(user => {
                if (user.id) {
                    this.props.loadUser(user);
                    this.props.onRouteChange('signin');
                }
            })
    }

    render(){
        return (
            <article className="db fw6 lh-copy f2" style={{display: 'flex', justifyContent: 'flex-center',}}>
                <main className="pa4 black-80">
                    <div className="measure">
                        <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
                            <legend className="f1 fw6 ph0 mh0">Share your recommended place!!</legend>
                            <div className="mt3">
                                <label className="db fw6 lh-copy f6" htmlFor="name">Name of the place</label>
                                <input
                                    className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100"
                                    type="text"
                                    name="name"
                                    id="name"
                                    onChange={this.onNameChange}
                                />
                            </div>

                            <form>
                                <label>
                                    Pick your favorite flavor:
                                    <select multiple={true}  onChange={this.onHandleChange}>
                                        <option value={"Ramen"}>Ramen</option>
                                        <option value="Cafe">Cafe</option>
                                        <option value="View">View</option>
                                        <option value="Shopping">Shopping</option>
                                        <option value="Izakaya">Izakaya</option>
                                        <option value="Japanese food">Japanese food</option>
                                    </select>
                                </label>
                            </form>

                            <div>
                                <p className='f3'>
                                    {'Put the URL of the best picture of the place!!'}
                                </p>
                                <div className='center'>
                                    <div className='form center pa4 br3 shadow-5'>
                                        <input className='f4 pa2 w-70 center' type='text' onChange={this.props.onInputChange}/>
                                        <button
                                            className='w-30 grow f4 link ph3 pv2 dib white bg-light-green'
                                            onClick={this.props.onButtonSubmit}
                                        >Submit</button>
                                    </div>
                                </div>
                            </div>

                            <form>
                                <label className="db fw6 lh-copy f6" htmlFor="name">Book</label>
                                <div className="form-check">
                                    <label>
                                        <input
                                            type="radio"
                                            name="react-tips"
                                            value="Yes"
                                            checked={this.state.book === "Yes"}
                                            onChange={this.handleBookChange}
                                            className="form-check-input"
                                        />
                                        Yes
                                    </label>
                                </div>
                                <div className="form-check">
                                    <label>
                                        <input
                                            type="radio"
                                            name="react-tips"
                                            value="No"
                                            checked={this.state.book === "No"}
                                            onChange={this.handleBookChange}
                                            className="form-check-input"
                                        />
                                        No
                                    </label>
                                </div>
                            </form>
                            <form>
                                <label className="db fw6 lh-copy f6" htmlFor="name">Attendance</label>
                                <div className="form-check">
                                    <label>
                                        <input
                                            type="radio"
                                            name="react-tips"
                                            value="Yes"
                                            checked={this.state.attendance === "Yes"}
                                            onChange={this.handleAttendanceChange}
                                            className="form-check-input"
                                        />
                                        Yes
                                    </label>
                                </div>
                                <div className="form-check">
                                    <label>
                                        <input
                                            type="radio"
                                            name="react-tips"
                                            value="No"
                                            checked={this.state.attendance === "No"}
                                            onChange={this.handleAttendanceChange}
                                            className="form-check-input"
                                        />
                                        No
                                    </label>
                                </div>
                            </form>
                            <div className="mt3">
                                <label className="db fw6 lh-copy f6" htmlFor="name">Class Style</label>
                                <input
                                    className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100"
                                    type="text"
                                    name="classstyle"
                                    id="classstyle"
                                    onChange={this.onClassChange}
                                />
                            </div>
                            <div className="mt3">
                                <label className="db fw6 lh-copy f6" htmlFor="name">Comments</label>
                                <input
                                    className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100"
                                    type="text"
                                    name="comments"
                                    id="comments"
                                    onChange={this.onCommentsChange}
                                />
                            </div>
                        </fieldset>


                        <div className="">
                            <input
                                onClick={this.onSubmitForm}
                                className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib"
                                type="submit"
                                value="Submit"
                            />
                        </div>
                    </div>
                </main>
            </article>

        );
    }

}

export default Form;
