import React from 'react';




const Card = ({ info } ) => {
    return(
        <div className='tc bg-light-green dib br3 pa3 ma2 grow bw2 shadow-5'>
            <div>
                <h2>{info.name}</h2>
                <img id='inputimage' alt='image' src={info.uri} width='500px' height='auto'/>
                <p>住所：{info.location}</p>
                <p>星：{info.rate}</p>
                <p>コメント：{info.comments}</p>
            </div>
        </div>
    );
}

export default Card;
