import React, { Component } from 'react';
import Particles from 'react-particles-js';
import './App.css';
import Navigation from './Components/Navigation/Navigation';
import Signin from './Components/Signin/Signin';
import Register from './Components/Register/Register';
import FaceRecognition from './Components/FaceRecognition/FaceRecognition';
import Logo from './Components/Logo/Logo';
import ImageLinkForm from './Components/ImageLinkForm/ImageLinkForm';
import Rank from './Components/Rank/Rank';
import Search from './Components/Search/Search';
import Infos from './Components/Infos/Infos';
import Form from './Components/Form/Form';
const particlesOptions = {
    particles: {
        number: {
            value: 150,
            density: {
                enable: true,
                value_area: 800
            }
        }
    }
}

const initialState = {
    input: '',
    imageUrl: '',
    box: {},
    searchedName: '',
    route: 'signin',
    isSignedIn: false,
    user: {
        id: '',
        name: '',
        email: '',
        entries: 0,
        joined: ''
    }
}

class App extends Component {
  constructor() {
      super();
      this.state = initialState; //interact parent with children
  }

  loadUser = (data) => {
      this.setState({user: {
          id: data.id,
          name: data.name,
          email: data.email,
          entries: data.entries,
          joined: data.joined
      }})
      console.log('loadUser data', data)
  }
    loadName = (data) => {
        this.setState({searchedName: data.name})
        console.log('searched data is...', data);
        console.log('searched name is...', this.state.searchedName)
    }


    calculateFaceLocation = (data) => {
      const clarifaiFace = data.outputs[0].data.regions[0].region_info.bounding_box;
      const image = document.getElementById('inputimage');
      const width = Number(image.width);
      const height = Number(image.height);
      return{
          leftCol: clarifaiFace.left_col * width,
          topRow: clarifaiFace.top_row * height,
          rightCol: width - (clarifaiFace.right_col * width),
          bottomRow: height - (clarifaiFace.bottom_row * height),
      }
}

displayFaceBox = (box) => {
      this.setState({box: box})
}

onInputChange = (event) => {
      this.setState({input: event.target.value}); // When event happens, get the event value
}

onButtonSubmit = () => {
      this.setState({imageUrl: this.state.input});
        fetch('http://localhost:3000/imageurl', { //fetch connects frontend with the server
            method: 'post',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({
             input: this.state.input
            })
        })
            .then(response => response.json()) //after "fetch", always do ".then( response => response.json())"
            .then(response => {
                if (response) {
                    fetch('http://localhost:3000/image', { //fetch connects frontend with the server
                        method: 'put',
                        headers: {'Content-Type': 'application/json'},
                        body: JSON.stringify({ // Send email and password updated at "onEmailChange" and "onPasswordChange" to the database through JSON.stringify
                            id: this.state.user.id
                        })
                    })
                        .then(response => response.json())
                        .then(count => {
                            this.setState(Object.assign(this.state.user, {entries: count}))
                        })
                        .catch(console.log)
                }
                this.displayFaceBox(this.calculateFaceLocation(response)) //.outputs[0].data.regions[0].region_info.bounding_box
            })
            .catch(err => console.log(err));
            // do something with response
}

onRouteChange = (route) => {
      if (route === 'signout') {
          this.setState(initialState)
      } else if (route === 'home') {
          this.setState({isSignedIn: true});
      }
      this.setState({route: route});
}




  render() {
      const { isSignedIn, imageUrl, route, box} = this.state;
    return (
      <div className="App">
        <Particles className='particles'
        params={particlesOptions}
        />
        <Navigation isSignedIn={isSignedIn} onRouteChange={this.onRouteChange}/>
          { route === 'home'
              ? <div>
                  < Logo />
                  < Rank name={this.state.user.name} entries={this.state.user.entries}/>
                  < ImageLinkForm
                      onInputChange={this.onInputChange}
                      onButtonSubmit={this.onButtonSubmit}
                  />
                  <FaceRecognition
                      box={box}
                      imageUrl={imageUrl}
                  />
                </div>
              :(
                  route === 'signin'
                  ? <Signin loadUser={this.loadUser} onRouteChange={this.onRouteChange}/>
                  :(
                      route === 'register'
                      ?<Register loadUser={this.loadUser} onRouteChange={this.onRouteChange}/>
                      :(
                          route === 'search'
                          ?< Search loadUser={this.loadUser}  onRouteChange={this.onRouteChange} loadName={this.loadName}/>
                          :(
                              route === 'infos'
                              ?< Infos loadUser={this.loadUser}  onRouteChange={this.onRouteChange} />
                              :(
                                     < Form loadUser={this.loadUser}  onRouteChange={this.onRouteChange} />
                                  )
                          )
                      )
                  )

              )
          }
      </div>
    );
  }
}

export default App;
