import React, { Component } from "react";
import Checkbox from "./Checkbox";

const category = ["ラーメン", "食事処", "景色","カフェ","公園", "クラブ", "バー", "勉強する場所", "ハイキング","買い物", "美容"];

class Form extends Component {
    constructor(props){
        super(props);
        this.state = {
            id: '',
            name: '',
            checkboxes: category.reduce(
                (options, option) => ({
                    ...options,
                    [option]: false
                }),
                {}
            ),
            ramen: '',
            food: '',
            view: '',
            cafe: '',
            park:'',
            club: '',
            bar: '',
            study: '',
            hiking: '',
            shopping: '',
            beauty:'',
            location: '',
            rate:'',
            comments: ''
        };
        // console.log(this.state.checkboxes)
    }



    onNameChange = (event) => {
        this.setState({name: event.target.value}) // updated signInEmail from <input />
    }

    onLocationChange = (event) => {
        this.setState({location: event.target.value}) // updated signInEmail from <input />
    }

    onCommentsChange = (event) => {
        this.setState({comments: event.target.value}) // updated signInEmail from <input />
    }



    handleRateChange = changeEvent => {
        this.setState({
            rate: changeEvent.target.value
        });
    };


    onSubmitForm =() => {
        fetch('http://localhost:3000/register', {
            method: 'post',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({ // Send email and password updated at "onEmailChange" and "onPasswordChange" to the database through JSON.stringify
                name: this.state.name,
                coursenumber:this.state.coursenumber,
                easiness: this.state.easiness,
                contents: this.state.contents,
                grade: this.state.grade,
                book: this.state.book,
                attendance:this.state.attendance,
                classstyle: this.state.classstyle,
                comments: this.state.comments
            })
        })
            .then(response => response.json()) // Get response through json, and get data by ".then"
            .then(user => {
                if (user.id) {
                    this.props.loadUser(user);
                    this.props.onRouteChange('signin');
                }
            })
    }


//Check box

    selectAllCheckboxes = isSelected => {
        Object.keys(this.state.checkboxes).forEach(checkbox => {
            // BONUS: Can you explain why we pass updater function to setState instead of an object?
            this.setState(prevState => ({
                checkboxes: {
                    ...prevState.checkboxes,
                    [checkbox]: isSelected
                }
            }));
        });
    };

    selectAll = () => this.selectAllCheckboxes(true);

    deselectAll = () => this.selectAllCheckboxes(false);

    handleCheckboxChange = changeEvent => {
        const { name } = changeEvent.target;

        this.setState(prevState => ({
            checkboxes: {
                ...prevState.checkboxes,
                [name]: !prevState.checkboxes[name]
            }
        }));
    };

    handleFormSubmit = formSubmitEvent => {
        formSubmitEvent.preventDefault();

        Object.keys(this.state.checkboxes)
            .filter(checkbox => this.state.checkboxes[checkbox])
            .forEach(checkbox => {
                console.log(checkbox, "is selected.");
                console.log('the object is like ',this.state.checkboxes);
                console.log('the ramen is like ',this.state.checkboxes.Ramen);
            });
    };

    createCheckbox = option => (
        <Checkbox
            label={option}
            isSelected={this.state.checkboxes[option]}
            onCheckboxChange={this.handleCheckboxChange}
            key={option}
        />
    );

    createCheckboxes = () => category.map(this.createCheckbox);



    render(){
        return (
            <article className="db fw6 lh-copy f2" style={{display: 'flex', justifyContent: 'flex-center',}}>
                <main className="pa4 black-80">
                    <div className="measure">
                        <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
                            <legend className="f1 fw6 ph0 mh0">オススメの場所をシェアハピして下さい！！</legend>
                            <div className="mt3">
                                <label className="db fw6 lh-copy f6" htmlFor="name">場所の名前は？</label>
                                <input
                                    className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100"
                                    type="text"
                                    name="name"
                                    id="name"
                                    onChange={this.onNameChange}
                                />
                            </div>

                            <div className="container">
                                <div className="row mt-5">
                                    <div className="col-sm-12">
                                        <label className="db fw6 lh-copy f6" htmlFor="name">ジャンルは？　（複数選択可能）</label>
                                        <form onSubmit={this.handleFormSubmit}>
                                            {this.createCheckboxes()}

                                            <div className="form-group mt-2">
                                                <button
                                                    type="button"
                                                    className="btn btn-outline-primary mr-2"
                                                    onClick={this.selectAll}
                                                >
                                                    Select All
                                                </button>
                                                <button
                                                    type="button"
                                                    className="btn btn-outline-primary mr-2"
                                                    onClick={this.deselectAll}
                                                >
                                                    Deselect All
                                                </button>
                                                <button type="submit" className="btn btn-primary">
                                                    Save
                                                </button>
                                            </div>
                                        </form>
                                    </div>
                                </div>
                            </div>


                            <div>
                                <p className='f3'>
                                    {'Put the URL of the best picture of the place!!'}
                                </p>
                                <div className='center'>
                                    <div className='form center pa4 br3 shadow-5'>
                                        <input className='f4 pa2 w-70 center' type='text' onChange={this.props.onInputChange}/>
                                        <button
                                            className='w-30 grow f4 link ph3 pv2 dib white bg-light-green'
                                            onClick={this.props.onButtonSubmit}
                                        >Submit</button>
                                    </div>
                                </div>
                            </div>

                            <form>
                                <label className="db fw6 lh-copy f6" htmlFor="name">Rate</label>
                                    <label>
                                        <input
                                            type="radio"
                                            name="react-tips"
                                            value="5"
                                            checked={this.state.rate === "5"}
                                            onChange={this.handleRateChange}
                                            className="form-check-input"
                                        />
                                        5
                                    </label>
                                <p> </p>
                                    <label>
                                        <input
                                            type="radio"
                                            name="react-tips"
                                            value="4"
                                            checked={this.state.rate === "4"}
                                            onChange={this.handleRateChange}
                                            className="form-check-input"
                                        />
                                        4
                                    </label>
                                <label>
                                    <input
                                        type="radio"
                                        name="react-tips"
                                        value="3"
                                        checked={this.state.rate === "3"}
                                        onChange={this.handleRateChange}
                                        className="form-check-input"
                                    />
                                    3
                                </label>
                                <label>
                                    <input
                                        type="radio"
                                        name="react-tips"
                                        value="2"
                                        checked={this.state.rate === "2"}
                                        onChange={this.handleRateChange}
                                        className="form-check-input"
                                    />
                                    2
                                </label>
                                <label>
                                    <input
                                        type="radio"
                                        name="react-tips"
                                        value="1"
                                        checked={this.state.rate === "1"}
                                        onChange={this.handleRateChange}
                                        className="form-check-input"
                                    />
                                    1
                                </label>
                            </form>
                            <div className="mt3">
                                <label className="db fw6 lh-copy f6" htmlFor="name">Location</label>
                                <input
                                    className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100"
                                    type="text"
                                    name="location"
                                    id="location"
                                    onChange={this.onLocationChange}
                                />
                            </div>
                            <div className="mt3">
                                <label className="db fw6 lh-copy f6" htmlFor="name">Comments</label>
                                <input
                                    className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100"
                                    type="text"
                                    name="comments"
                                    id="comments"
                                    onChange={this.onCommentsChange}
                                />
                            </div>
                        </fieldset>


                        <div className="">
                            <input
                                onClick={this.onSubmitForm}
                                className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib"
                                type="submit"
                                value="Submit"
                            />
                        </div>
                    </div>
                </main>
            </article>

        );
    }

}

export default Form;
