import React, { Component } from 'react';
import Particles from 'react-particles-js';
import Clarifai from 'clarifai';
import './App.css';
import Navigation from './Components/Navigation/Navigation';
import FaceRecognition from './Components/FaceRecognition/FaceRecognition';
import Logo from './Components/Logo/Logo';
import ImageLinkForm from './Components/ImageLinkForm/ImageLinkForm';
import Rank from './Components/Rank/Rank';

const app = new Clarifai.App({
    apiKey: '3b2d6a108b6549f2baa43151f6f6bd22'
});

const particlesOptions = {
    particles: {
        number: {
            value: 150,
            density: {
                enable: true,
                value_area: 800
            }
        }
    }
}

class App extends Component {
  constructor() {
      super();
      this.state = {
          input: '', //interact parent with children
          imageUrl: '',
          box: {},
      }
  }

calculateFaceLocation = (data) => {
      const clarifaiFace = data.outputs[0].data.regions[0].region_info.bounding_box;
      const image = document.getElementById('inputimage');
      const width = Number(image.width);
      const height = Number(image.height);
      return{
          leftCol: clarifaiFace.left_col * width,
          topRow: clarifaiFace.top_row * hight,
          rightCol: width - (clarifaiFace.right_col * width),
          bottomRow: height - (clarifaiFace.bottom.row * height),

      }
}

displayFaceBox = (box) => {
      this.setState({box: box})
}

onInputChange = (event) => {
      this.setState({input: event.target.value}); // When event happens, get the event value
}

onButtonSubmit = () => {
      this.setState({imageUrl: this.state.input});
        app.models
        .predict(
            Clarifai.FACE_DETECT_MODEL,
            this.state.input)
            .then(response => this.calculateFaceLocation(response)) //.outputs[0].data.regions[0].region_info.bounding_box
            .catch(err => console.log(err));
            // do something with response
        }


  render() {
    return (
      <div className="App">
        <Particles className='particles'
        params={particlesOptions}
        />
        <Navigation />
        <Logo />
        <Rank />
        <ImageLinkForm
            onInputChange={this.onInputChange}
            onButtonSubmit={this.onButtonSubmit}
        />
        <FaceRecognition
            imageUrl={this.state.imageUrl}
        />
      </div>
    );
  }
}

export default App;
