import React, { Component } from "react";


class Form extends Component {
    constructor(props){
        super(props);
        this.state = {
            id: '',
            name: '',
            course:'',
            easiness: '',
            contents: ''
            // courseNumber: '',
            // grade: '',
            // book: '',
            // attendance: '',
            // classStyle: '',
            // comments: ''
        }
    }
    onNameChange = (event) => {
        this.setState({name: event.target.value}) // updated signInEmail from <input />
    }

    onCourseChange = (event) => {
        this.setState({course: event.target.value}) // updated signInEmail from <input />
    }

    // onCourseNumberChange = (event) => {
    //     this.setState({name: event.target.value}) // updated signInEmail from <input />
    // }
    // onGradeChange = (event) => {
    //     this.setState({email: event.target.value}) // updated signInEmail from <input />
    // }
    // onBookChange = (event) => {
    //     this.setState({password: event.target.value}) // updated signInPassword from <input />
    // }
    // onAttendanceSChange = (event) => {
    //     this.setState({password: event.target.value}) // updated signInPassword from <input />
    // }
    // onClassStyleChange = (event) => {
    //     this.setState({email: event.target.value}) // updated signInEmail from <input />
    // }
    // onCommentsChange = (event) => {
    //     this.setState({password: event.target.value}) // updated signInPassword from <input />
    // }

    handleContentsChange = changeEvent => {
        this.setState({
            contents: changeEvent.target.value
        });
        console.log(this.state.contents)
    };

    handleOptionChange = changeEvent => {
        this.setState({
        easiness: changeEvent.target.value
        });
        console.log(this.state.easiness)
    };

    onSubmitSignIn =() => {
        fetch('http://localhost:3000/professors', {
            method: 'post',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({ // Send email and password updated at "onEmailChange" and "onPasswordChange" to the database through JSON.stringify
                name: this.state.name,
                easiness: this.state.easiness,
                contents: this.state.contents
            })
        })
            .then(response => response.json()) // Get response through json, and get data by ".then"
            .then(user => {
                if (user.id) {
                    this.props.loadUser(user);
                    this.props.onRouteChange('signin');
                }
            })
    }

    render(){
        return (
            <article className="br3 ba b--black-10 mv5 w-100 w-50-m w-25-l mw5 shadow-5 center">
                <main className="pa4 black-80">
                    <div className="measure">
                        <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
                            <legend className="f1 fw6 ph0 mh0">Rate Your Professor</legend>
                            <div className="mt3">
                                <label className="db fw6 lh-copy f6" htmlFor="name">Name</label>
                                <input
                                    className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100"
                                    type="text"
                                    name="name"
                                    id="name"
                                    onChange={this.onNameChange}
                                />
                            </div>
                            <div className="mt3">
                                <label className="db fw6 lh-copy f6" htmlFor="name">Course Number</label>
                                <input
                                    className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100"
                                    type="text"
                                    name="course"
                                    id="course"
                                    onChange={this.onCourseChange}
                                />
                            </div>

                            <form>
                                <label className="db fw6 lh-copy f6" htmlFor="name">Easiness</label>
                                <div className="form-check">
                                    <label>
                                        <input
                                            type="radio"
                                            name="react-tips"
                                            value="super easy"
                                            checked={this.state.easiness === "super easy"}
                                            onChange={this.handleOptionChange}
                                            className="form-check-input"
                                        />
                                        Super easy
                                    </label>
                                </div>
                                <div className="form-check">
                                    <label>
                                        <input
                                            type="radio"
                                            name="react-tips"
                                            value="easy"
                                            checked={this.state.easiness === "easy"}
                                            onChange={this.handleOptionChange}
                                            className="form-check-input"
                                        />
                                        Easy
                                    </label>
                                </div>
                                <div className="form-check">
                                    <label>
                                        <input
                                            type="radio"
                                            name="react-tips"
                                            value="normal"
                                            checked={this.state.easiness === "normal"}
                                            onChange={this.handleOptionChange}
                                            className="form-check-input"
                                        />
                                        Normal
                                    </label>
                                </div>
                                <div className="form-check">
                                    <label>
                                        <input
                                            type="radio"
                                            name="react-tips"
                                            value="hard"
                                            checked={this.state.easiness === "hard"}
                                            onChange={this.handleOptionChange}
                                            className="form-check-input"
                                        />
                                        Hard
                                    </label>
                                </div>
                                <div className="form-check">
                                    <label>
                                        <input
                                            type="radio"
                                            name="react-tips"
                                            value="super hard"
                                            checked={this.state.easiness === "super hard"}
                                            onChange={this.handleOptionChange}
                                            className="form-check-input"
                                        />
                                        Super Hard
                                    </label>
                                </div>
                            </form>

                            <form>
                                <label className="db fw6 lh-copy f6" htmlFor="name">Contents</label>
                                <div className="form-check">
                                    <label>
                                        <input
                                            type="radio"
                                            name="contents"
                                            value="excellent"
                                            checked={this.state.contents === "excellent"}
                                            onChange={this.handleContentsChange}
                                            className="form-check-input"
                                        />
                                        Excellent
                                    </label>
                                </div>
                                <div className="form-check">
                                    <label>
                                        <input
                                            type="radio"
                                            name="contents"
                                            value="great"
                                            checked={this.state.contents === "great"}
                                            onChange={this.handleContentsChange}
                                            className="form-check-input"
                                        />
                                        Great
                                    </label>
                                </div>
                                <div className="form-check">
                                    <label>
                                        <input
                                            type="radio"
                                            name="contents"
                                            value="normal"
                                            checked={this.state.contents === "normal"}
                                            onChange={this.handleContentsChange}
                                            className="form-check-input"
                                        />
                                        Normal
                                    </label>
                                </div>
                                <div className="form-check">
                                    <label>
                                        <input
                                            type="radio"
                                            name="contents"
                                            value="poor"
                                            checked={this.state.contents === "poor"}
                                            onChange={this.handleContentsChange}
                                            className="form-check-input"
                                        />
                                        Poor
                                    </label>
                                </div>
                                <div className="form-check">
                                    <label>
                                        <input
                                            type="radio"
                                            name="contents"
                                            value="very poor"
                                            checked={this.state.contents === "very poor"}
                                            onChange={this.handleContentsChange}
                                            className="form-check-input"
                                        />
                                        Very poor
                                    </label>
                                </div>
                            </form>

                        </fieldset>
                        <div className="">
                            <input
                                onClick={this.onSubmitSignIn}
                                className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib"
                                type="submit"
                                value="Register"
                            />
                        </div>
                    </div>
                </main>
            </article>

        );
    }

}

export default Form;
