import React from 'react';
import CardList from '../CardList'; //child
import SearchBox from '../SearchBox' //child
import Scroll from '../Scroll'

class Search extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            professors: [],
            searchfield: ''
        }
    }
    componentDidMount() {
        fetch('http://localhost:3000/prof', {
            method: 'post',
            headers: {'Content-Type': 'application/json'},
            body: JSON.stringify({ // Send email and password updated at "onEmailChange" and "onPasswordChange" to the database through JSON.stringify
                name: this.state.searchfield,
            })
        })
            .then(response => response.json()) // Get response through json, and get data by ".then"
            .then(user => this.setState({professor: user.name}))
}

    onSearchChange = (event) => { //whenever it gets changed
        this.setState({searchfield: event.target.value}) //update "serachfiled is event.target.value"
    }
    render() {
        const {professors, searchfield} = this.state;
        const filterdProfs = professors.filter(prof => {
            //"filter" is a function to go thorough array in "robots from State", having a parameter "robot"
            return prof.name.toLowerCase().includes(searchfield.toLowerCase())
            //return the "robot"'s name if it is included"serachfield's strings, setting lowercase every words
        })
        return !professors.length ?
            <h1>Loading</h1>
            :(
                <div className='tc'>
                    <h1>Easy or Hard</h1>
                    <SearchBox onSearchChange={this.onSearchChange}/>
                    <Scroll>
                        <CardList professors={filterdProfs}/>
                    </Scroll>
                </div>
            );
    }
}

export default Search;
