import React, { Component } from 'react';
import Particles from 'react-particles-js';
import './App.css';
import Navigation from './Components/Navigation/Navigation';
import Signin from './Components/Signin/Signin';
import FaceRecognition from './Components/FaceRecognition/FaceRecognition';
import Logo from './Components/Logo/Logo';
import ImageLinkForm from './Components/ImageLinkForm/ImageLinkForm';
import Category from './Components/Search/Category';
import Infos from './Components/Infos/Infos';
import Form from './Components/Form/Form';

const particlesOptions = {
    particles: {
        number: {
            value: 150,
            density: {
                enable: true,
                value_area: 800
            }
        }
    }
}

const initialState = {
    category: '',
    route: 'home',
    isSignedIn: true,
    user: {
        id: '',
        name: '',
        email: '',
        joined: ''
    }
}

class App extends Component {
  constructor() {
      super();
      this.state = initialState; //interact parent with children
  }

  loadUser = (data) => {
      this.setState({user: {
          id: data.id,
          name: data.name,
          email: data.email,
          joined: data.joined
      }})
  }

    loadCategory = (cat) => {
        this.setState({
            category: cat
        })
        console.log(cat)
    }







onRouteChange = (route) => {
      this.setState({route: route});
}


  render() {
      const { isSignedIn, route} = this.state;
    return (
      <div className="App">
        <Particles className='particles'
        params={particlesOptions}
        />
        <Navigation isSignedIn={isSignedIn} onRouteChange={this.onRouteChange}/>
          { route === 'home'
              ? <div>
                  < Logo />



                </div>
              :(
                          route === 'category'
                          ?< Category loadUser={this.loadUser} onRouteChange={this.onRouteChange} loadCategory={this.loadCategory}/>
                          :(
                              route === 'infos'
                              ?< Infos loadUser={this.loadUser}  onRouteChange={this.onRouteChange} loadCategory={this.loadCategory} category={this.state.category} />
                              :(
                                     < Form loadUser={this.loadUser}  onRouteChange={this.onRouteChange} />
                                  )
                          )
                  )
          }
      </div>
    );
  }
}

export default App;
