import React, { Component } from 'react';
import CardList from '../CardList'; //child
import SearchBox from '../SearchBox' //child
import Scroll from '../Scroll'

class Search extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            professors: [],
            searchfield: ''
        }
    }

    componentDidMount() {
        fetch('https://jsonplaceholder.typicode.com/users') //made Ajax request, using the fetch API
            .then(response => response.json()) //got the response, which was "promise", so do ".then()" --> anytime I use "fetch", use "response.json()" to convert "Json" to something we can use Javascript
            .then(users => this.setState({professors: users}));

    //     fetch('http://localhost:3000/prof',{ //fetch connects frontend with the server
    //         method: 'get',
    //         headers: {'Content-Type': 'application/json'},
    //         body: JSON.stringify({ // Send something updated at "onEmailChange" and "onPasswordChange" to the database through JSON.stringify
    //             id: this.state.professors.name
    //         })
    //         .then(response => response.json()) // Get response through json, and get data by ".then"
    //         .then(users => this.setState({professors: users}))
    //
    // })
    }

    onSearchChange = (event) => { //whenever it gets changed
        this.setState({searchfield: event.target.value}) //update "serachfiled is event.target.value"
    }

    // filterdProfs = () =>{
    //     this.state.professors.filter(prof => {
    //     console.log(prof);
    //     return prof.name.toLowerCase().includes(this.state.searchfield.toLowerCase())
    // })
    // }

    render() {
        const filterdProfs = this.state.professors.filter(prof => {
            console.log(prof);
            //"filter" is a function to go thorough array in "robots from State", having a parameter "robot"
            return prof.name.toLowerCase().includes(this.state.searchfield.toLowerCase())
            //return the "robot"'s name if it is included"serachfield's strings, setting lowercase every words
        })
        return (
            <div className="measure">
                <label className="db fw6 lh-copy f2" htmlFor="name">Easy or Hard</label>
                <SearchBox onSearchChange={this.onSearchChange}/>
                <Scroll>
                    <CardList professors={filterdProfs}/>
                </Scroll>
            </div>
        );
    }
}


export default Search;
